mapper.select:
SELECT BUILD_SEQ , BUILD_NAME , (SELECT CODE_NAME FROM t_code tc WHERE LOCATION_CD = tc.CODE_VAL) LOCATION_CD , CONCAT(ADDR1, ' ', ADDR2) AS ADDRESS , GROUND_FLOOR , BASE_FLOOR , CONCAT(STD_POINT1, ' / ', STD_POINT2) AS POINT , STD_POINT1 , STD_POINT2 , AREA_POINT1 , AREA_POINT2 , REG_DATE , REG_USER FROM t_building WHERE 1=1
AND BUILD_NAME LIKE CONCAT('%', #{buildName}, '%')
AND DATE_FORMAT(REG_DATE, '%Y%m%d') >= #{scRegDtSt}
AND DATE_FORMAT(REG_DATE, '%Y%m%d') <= #{scRegDtEd}
AND LOCATION_CD = #{locationCd}
AND IS_USE = #{isUse}

SELECT
    fi.FILE_PATH
FROM t_building tb
         LEFT JOIN t_floor_info fi ON tb.BUILD_SEQ = fi.BUILD_SEQ
WHERE fi.BUILD_SEQ = #{buildSeq}
  AND fi.fLOOR = #{floor}

SELECT
    FLOOR_SEQ
FROM t_floor_info
WHERE BUILD_SEQ = #{buildSeq}
  AND FLOOR = #{floor}

mapper.insert:
INSERT INTO t_device
( BUILD_SEQ
  , FLOOR_SEQ
  , FLOOR
  , TYPE_CD
  , LOCATION
  , POINT1
  , POINT2
  , REG_DATE
  , REG_USER
  , STATE_CD
  , IS_USE
  , MEMO
) VALUES (
  #{buildSeq}
  , (SELECT IFNULL(MAX(td.BUILD_SEQ), 0) + 1 FROM t_device td)
  , #{floor}
  , #{typeCd}
  , #{locationCd}
  , #{point1}
  , #{point2}
  , NOW()
  , 999
  , #{stateCd}
  , #{isUse}
  , #{memo}
)
