mapper.select:
SELECT g.geofence_seq , g.build_seq , g.floor_seq , g.floor , g.geo_name , c1.code_name AS typeCdName , c2.code_name AS locationName , g.state_cd , b.build_name , g.reg_date , g.reg_user FROM t_geofence g LEFT JOIN t_code c1 ON g.type_cd = c1.code_val AND c1.group_cd = 'FIG' LEFT JOIN t_code c2 ON g.location_cd = c2.code_val AND c2.group_cd = 'DLO' LEFT JOIN t_building b ON g.build_seq = b.build_seq WHERE 1=1
AND b.build_name LIKE CONCAT('%', #{buildName}, '%')
AND DATE_FORMAT(g.reg_date, '%Y%m%d') >= #{scRegDtSt}
AND DATE_FORMAT(g.reg_date, '%Y%m%d') <= #{scRegDtEd}
AND g.floor = #{floor}

mapper.insert:
INSERT INTO t_geofence
( build_seq
  , floor_seq
  , floor
  , geo_name
  , type_cd
  , location_cd
  , state_cd
  , reg_date
  , reg_user
) VALUES (
  #{buildSeq}
  , #{floorSeq}
  , #{floor}
  , #{geoName}
  , #{typeCd}
  , #{locationCd}
  , #{stateCd}
  , NOW()
  , 0
);

INSERT INTO t_geofence_info
( geofence_seq
  , set_point
  , area_point
  , radius
) VALUES

( #{geofenceSeq}
  , 

        ST_GeomFromText(

            ${setPointX} ${setPointY}

        , 3857)


        null


  , ST_GeomFromText (


            ${item.pointX} ${item.pointY}




            ${item.pointX} ${item.pointY}


    , 3857)
  , #{radius}
)
