mapper.select:
SELECT ICON_SEQ , (SELECT CODE_NAME FROM t_code tc WHERE TI.GROUP_CD = tc.GROUP_CD AND TI.CODE_VAL = tc.CODE_VAL) codeName , ICON_NAME , ICON_PATH , REG_DATE , MODI_DATE FROM t_icon TI WHERE 1=1
AND TI.ICON_NAME LIKE CONCAT('%', #{iconName}, '%')
AND DATE_FORMAT(TI.REG_DATE, '%Y%m%d') >= #{scRegDtSt}
AND DATE_FORMAT(TI.REG_DATE, '%Y%m%d') <= #{scRegDtEd}
AND TI.CODE_VAL = #{codeVal}

SELECT COUNT(ICON_SEQ)
FROM t_icon
WHERE GROUP_CD = #{groupCd}
AND CODE_VAL = #{codeVal}

mapper.insert:
INSERT INTO t_icon
( ICON_NAME
  , GROUP_CD
  , CODE_VAL
  , ICON_PATH
  , USER_SEQ
  , REG_DATE
  , MODI_SEQ
  , MODI_DATE
) VALUES (
  #{iconName}
  , #{groupCd}
  , #{codeVal}
  , #{iconPath}
  , #{userSeq}
  , NOW()
  , #{userSeq}
  , NOW()
)

mapper.update:
UPDATE t_icon
SET ICON_NAME   = #{iconName}
  , GROUP_CD    = #{groupCd}
  , CODE_VAL    = #{codeVal}
  , ICON_PATH   = #{iconPath}
  , MODI_SEQ    = #{userSeq}
  , MODI_DATE   = NOW()
