mapper.select:
SELECT COUNT(ICON_SEQ)
FROM t_icon
WHERE GROUP_CD = #{groupCd}
AND CODE_VAL = #{codeVal}

mapper.insert:
INSERT INTO t_icon
( ICON_NAME
  , GROUP_CD
  , CODE_VAL
  , ICON_PATH
  , USER_SEQ
  , REG_DATE
  , MODI_SEQ
  , MODI_DATE
) VALUES (
  #{iconName}
  , #{groupCd}
  , #{codeVal}
  , #{iconPath}
  , #{userSeq}
  , NOW()
  , #{userSeq}
  , NOW()
)
