mapper.select:
SELECT TU.USER_SEQ , TU.USER_NAME , TU.USER_ID , (SELECT CODE_NAME FROM t_code TL WHERE TU.SEX_CD = TL.CODE_VAL) SEX_CD , TU.REG_DATE , TU.LOGIN_DATE FROM t_user TU WHERE 1=1
AND TU.USER_NAME LIKE CONCAT('%', #{userName}, '%')
AND TU.USER_ID LIKE CONCAT('%', #{userId}, '%')
AND DATE_FORMAT(TU.REG_DATE, '%Y%m%d') >= #{scRegDtSt}
AND DATE_FORMAT(TU.REG_DATE, '%Y%m%d') <= #{scRegDtEd}

mapper.insert:
INSERT INTO t_user
( USER_ID
, PASSWORD
, USER_NAME
, SEX_CD
, BIRTHDAY
, ADDR1
, ADDR2
, TEL
, EMAIL
, STATE_CD
, IS_USE
, MEMO
, REG_DATE
, LOGIN_DATE
) VALUES (
  #{userId}
, #{password}
, #{userName}
, #{sexCd}
, #{birthday}
, #{addr1}
, #{addr2}
, #{tel}
, #{email}
, #{stateCd}
, #{isUse}
, #{memo}
, NOW()
, NOW()
)

mapper.update:
UPDATE t_user
SET PASSWORD = #{password}
    , USER_NAME = #{userName}
    , SEX_CD = #{sexCd}
    , BIRTHDAY = #{birthday}
    , ADDR1 = #{addr1}
    , ADDR2 = #{addr2}
    , IS_USE = #{isUse}
    , MEMO = #{memo}
WHERE USER_SEQ = #{userSeq}
